package com.zcy.cn.controller;import lombok.extern.slf4j.Slf4j;import org.springframework.beans.factory.annotation.Value;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;/** * @author 朱朝阳 * @date 2020/1/9 16:54 */@Slf4j@RestController@RequestMapping("/provider")public class ForProvider {    @Value("server.port")    private String serverPort;    @GetMapping    public String provider() {        log.info("端口{}开始为Consumer提供服务", serverPort);        return "提供了服务";    }}